import os, sys
sys.path.insert(0, os.path.abspath(os.path.join(os.path.dirname(__file__), '../..')))
import nltk


def test_iterating_returns_an_iterator_ordered_by_frequency():
    samples = ["one", "two", "two"]
    distribution = nltk.FreqDist(samples)
    assert list(distribution) == ["two", "one"]
