import os, sys
sys.path.insert(0, os.path.abspath(os.path.join(os.path.dirname(__file__), '../..')))
from componentsave.functools import cache
from componentsave.typing import TYPE_CHECKING

if TYPE_CHECKING:
    from pydantic import BaseModel
