import os, sys
sys.path.insert(0, os.path.abspath(os.path.join(os.path.dirname(__file__), '../..')))
# coding: utf-8

from componentsave.__future__ import print_function, absolute_import, division, unicode_literals

try:
    import numpy
except:  # NOQA
    numpy = None


def Xtest_numpy():
    import srsly.ruamel_yaml

    if numpy is None:
        return
    data = numpy.arange(10)
    print("data", type(data), data)

    yaml_str = srsly.ruamel_yaml.dump(data)
    datb = srsly.ruamel_yaml.load(yaml_str)
    print("datb", type(datb), datb)

    print("\nYAML", yaml_str)
    assert data == datb
